import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Card, Table, Button, Badge, Form, Modal, Alert } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import { filesAPI } from '../services/api';
import { useAuth } from '../context/AuthContext';
import Header from '../components/common/Header';
import ProtectedRoute from '../components/common/ProtectedRoute';
import './Files.css';

const Files = () => {
  const [files, setFiles] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showCreateModal, setShowCreateModal] = useState(false);
  const [filterStatus, setFilterStatus] = useState('');
  const { isAdmin, isEmployee } = useAuth();

  useEffect(() => {
    fetchFiles();
  }, [filterStatus]);

  const fetchFiles = async () => {
    try {
      const params = filterStatus ? { status: filterStatus } : {};
      const response = await filesAPI.getAll(params);
      setFiles(response.data.data);
    } catch (error) {
      console.error('Error fetching files:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleStatusFilter = (status) => {
    setFilterStatus(status);
  };

  if (loading) {
    return (
      <ProtectedRoute>
        <Header />
        <div className="d-flex justify-content-center align-items-center" style={{ height: '50vh' }}>
          <div className="spinner-border text-primary" role="status">
            <span className="visually-hidden">جاري التحميل...</span>
          </div>
        </div>
      </ProtectedRoute>
    );
  }

  return (
    <ProtectedRoute>
      <div className="files-page">
        <Header />
        
        <Container fluid className="page-header bg-light py-4">
          <Container>
            <Row className="align-items-center">
              <Col>
                <h1 className="h2 mb-0">إدارة الملفات</h1>
                <p className="text-muted mb-0">عرض وإدارة جميع ملفات الاستخلاص</p>
              </Col>
              <Col xs="auto">
                {(isAdmin() || isEmployee()) && (
                  <Button 
                    variant="primary" 
                    onClick={() => setShowCreateModal(true)}
                  >
                    <i className="fas fa-plus me-2"></i>
                    إضافة ملف جديد
                  </Button>
                )}
              </Col>
            </Row>
          </Container>
        </Container>

        <Container className="py-4">
          {/* فلاتر */}
          <Card className="mb-4">
            <Card.Body>
              <Row className="align-items-center">
                <Col md={6}>
                  <h6 className="mb-0">تصفية حسب الحالة:</h6>
                </Col>
                <Col md={6}>
                  <div className="filter-buttons">
                    <Button
                      variant={filterStatus === '' ? 'primary' : 'outline-primary'}
                      size="sm"
                      onClick={() => handleStatusFilter('')}
                      className="me-2"
                    >
                      الكل
                    </Button>
                    <Button
                      variant={filterStatus === 'new' ? 'primary' : 'outline-primary'}
                      size="sm"
                      onClick={() => handleStatusFilter('new')}
                      className="me-2"
                    >
                      جديد
                    </Button>
                    <Button
                      variant={filterStatus === 'in_progress' ? 'primary' : 'outline-primary'}
                      size="sm"
                      onClick={() => handleStatusFilter('in_progress')}
                      className="me-2"
                    >
                      قيد العمل
                    </Button>
                    <Button
                      variant={filterStatus === 'paid' ? 'primary' : 'outline-primary'}
                      size="sm"
                      onClick={() => handleStatusFilter('paid')}
                    >
                      مدفوع
                    </Button>
                  </div>
                </Col>
              </Row>
            </Card.Body>
          </Card>

          {/* جدول الملفات */}
          <Card>
            <Card.Header>
              <Row className="align-items-center">
                <Col>
                  <h5 className="mb-0">قائمة الملفات</h5>
                </Col>
                <Col xs="auto">
                  <small className="text-muted">
                    إجمالي الملفات: <strong>{files.length}</strong>
                  </small>
                </Col>
              </Row>
            </Card.Header>
            <Card.Body className="p-0">
              {files.length > 0 ? (
                <div className="table-responsive">
                  <Table hover className="mb-0">
                    <thead className="table-light">
                      <tr>
                        <th>رقم الملف</th>
                        <th>المدين</th>
                        <th>العميل</th>
                        <th>المبلغ الإجمالي</th>
                        <th>تاريخ الإيداع</th>
                        <th>الحالة</th>
                        <th>الإجراءات</th>
                      </tr>
                    </thead>
                    <tbody>
                      {files.map((file) => (
                        <FileRow key={file.id} file={file} onUpdate={fetchFiles} />
                      ))}
                    </tbody>
                  </Table>
                </div>
              ) : (
                <div className="text-center py-5">
                  <i className="fas fa-folder-open fa-3x text-muted mb-3"></i>
                  <h5 className="text-muted">لا توجد ملفات</h5>
                  <p className="text-muted">لم يتم إضافة أي ملفات حتى الآن</p>
                  {(isAdmin() || isEmployee()) && (
                    <Button 
                      variant="primary" 
                      onClick={() => setShowCreateModal(true)}
                    >
                      إضافة أول ملف
                    </Button>
                  )}
                </div>
              )}
            </Card.Body>
          </Card>
        </Container>

        {/* مودال إضافة ملف جديد */}
        <CreateFileModal 
          show={showCreateModal}
          onHide={() => setShowCreateModal(false)}
          onSuccess={() => {
            setShowCreateModal(false);
            fetchFiles();
          }}
        />
      </div>
    </ProtectedRoute>
  );
};

// مكون صف الملف - AVEC LES MODIFICATIONS DE COULEURS
const FileRow = ({ file, onUpdate }) => {
  const { isAdmin, isEmployee } = useAuth();

  // NOUVELLE FONCTION POUR LES COULEURS
  const getRowClassName = (file) => {
    if (file.status === 'paid') {
      return 'table-row-paid'; // أزرق فاتح للدفع الكامل
    } else if (file.status === 'partially_paid') {
      return 'table-row-partial'; // أصفر للدفع الجزئي
    } else if (file.status === 'closed') {
      return 'table-row-closed'; // رمادي للملفات المغلقة
    }
    return '';
  };

  const getStatusVariant = (status) => {
    const variants = {
      new: 'primary',
      in_progress: 'warning',
      paid: 'success',
      partially_paid: 'info',
      closed: 'secondary'
    };
    return variants[status] || 'secondary';
  };

  const getStatusText = (status) => {
    const texts = {
      new: 'جديد',
      in_progress: 'قيد العمل',
      paid: 'مدفوع',
      partially_paid: 'مدفوع جزئياً',
      closed: 'مغلق'
    };
    return texts[status] || status;
  };

  return (
    // APPLICATION DE LA COULEUR SUR LA LIGNE
    <tr className={getRowClassName(file)}>
      <td>
        <strong>#{file.id}</strong>
      </td>
      <td>
        <div>
          <div className="fw-semibold">{file.debtor}</div>
          {file.debt_proof && (
            <small className="text-muted">{file.debt_proof}</small>
          )}
        </div>
      </td>
      <td>
        {file.client_name || 'غير محدد'}
      </td>
      <td>
        <span className="fw-bold text-success">
          ${file.total_amount?.toLocaleString()}
        </span>
        {/* AFFICHAGE DU POURCENTAGE POUR LE PAIEMENT PARTIEL */}
        {file.status === 'partially_paid' && file.recovery_percentage > 0 && (
          <div className="recovery-percentage mt-1">
            <small className="text-warning fw-bold">
              {file.recovery_percentage.toFixed(1)}% مستخلص
            </small>
          </div>
        )}
      </td>
      <td>
        {new Date(file.deposit_date).toLocaleDateString('ar-EG')}
      </td>
      <td>
        <Badge bg={getStatusVariant(file.status)}>
          {getStatusText(file.status)}
        </Badge>
      </td>
      <td>
        <div className="action-buttons">
          <Button
            as={Link}
            to={`/files/${file.id}`}
            variant="outline-primary"
            size="sm"
            className="me-1"
          >
            <i className="fas fa-eye"></i>
          </Button>
          
          {(isAdmin() || isEmployee()) && (
            <>
              <Button
                as={Link}
                to={`/files/${file.id}/edit`}
                variant="outline-secondary"
                size="sm"
                className="me-1"
              >
                <i className="fas fa-edit"></i>
              </Button>
            </>
          )}
        </div>
      </td>
    </tr>
  );
};

// مودال إنشاء ملف جديد
const CreateFileModal = ({ show, onHide, onSuccess }) => {
  const [formData, setFormData] = useState({
    deposit_date: new Date().toISOString().split('T')[0],
    client_id: '',
    debtor: '',
    debt_proof: '',
    total_amount: '',
    commission: '',
    notes: ''
  });
  const [clients, setClients] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  useEffect(() => {
    fetchClients();
  }, []);

  const fetchClients = async () => {
    try {
      // سنضيف API للعملاء لاحقاً
    } catch (error) {
      console.error('Error fetching clients:', error);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    try {
      await filesAPI.create(formData);
      onSuccess();
    } catch (error) {
      setError(error.response?.data?.message || 'حدث خطأ أثناء إنشاء الملف');
    } finally {
      setLoading(false);
    }
  };

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  return (
    <Modal show={show} onHide={onHide} size="lg" centered>
      <Modal.Header closeButton>
        <Modal.Title>إضافة ملف جديد</Modal.Title>
      </Modal.Header>
      <Form onSubmit={handleSubmit}>
        <Modal.Body>
          {error && <Alert variant="danger">{error}</Alert>}
          
          <Row>
            <Col md={6}>
              <Form.Group className="mb-3">
                <Form.Label>تاريخ الإيداع *</Form.Label>
                <Form.Control
                  type="date"
                  name="deposit_date"
                  value={formData.deposit_date}
                  onChange={handleChange}
                  required
                />
              </Form.Group>
            </Col>
            <Col md={6}>
              <Form.Group className="mb-3">
                <Form.Label>العميل *</Form.Label>
                <Form.Select
                  name="client_id"
                  value={formData.client_id}
                  onChange={handleChange}
                  required
                >
                  <option value="">اختر العميل</option>
                  {clients.map(client => (
                    <option key={client.id} value={client.id}>
                      {client.name}
                    </option>
                  ))}
                </Form.Select>
              </Form.Group>
            </Col>
          </Row>

          <Row>
            <Col md={6}>
              <Form.Group className="mb-3">
                <Form.Label>اسم المدين *</Form.Label>
                <Form.Control
                  type="text"
                  name="debtor"
                  value={formData.debtor}
                  onChange={handleChange}
                  placeholder="أدخل اسم المدين"
                  required
                />
              </Form.Group>
            </Col>
            <Col md={6}>
              <Form.Group className="mb-3">
                <Form.Label>سند الدين</Form.Label>
                <Form.Control
                  type="text"
                  name="debt_proof"
                  value={formData.debt_proof}
                  onChange={handleChange}
                  placeholder="وصف سند الدين"
                />
              </Form.Group>
            </Col>
          </Row>

          <Row>
            <Col md={6}>
              <Form.Group className="mb-3">
                <Form.Label>المبلغ الإجمالي *</Form.Label>
                <Form.Control
                  type="number"
                  name="total_amount"
                  value={formData.total_amount}
                  onChange={handleChange}
                  placeholder="أدخل المبلغ"
                  required
                  min="0"
                  step="0.01"
                />
              </Form.Group>
            </Col>
            <Col md={6}>
              <Form.Group className="mb-3">
                <Form.Label>العمولة</Form.Label>
                <Form.Control
                  type="number"
                  name="commission"
                  value={formData.commission}
                  onChange={handleChange}
                  placeholder="العمولة"
                  min="0"
                  step="0.01"
                />
              </Form.Group>
            </Col>
          </Row>

          <Form.Group className="mb-3">
            <Form.Label>ملاحظات</Form.Label>
            <Form.Control
              as="textarea"
              rows={3}
              name="notes"
              value={formData.notes}
              onChange={handleChange}
              placeholder="أي ملاحظات إضافية"
            />
          </Form.Group>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={onHide}>
            إلغاء
          </Button>
          <Button variant="primary" type="submit" disabled={loading}>
            {loading ? 'جاري الحفظ...' : 'حفظ الملف'}
          </Button>
        </Modal.Footer>
      </Form>
    </Modal>
  );
};

export default Files;
