import React, { useState } from 'react';
import { Form, Button, Card, Alert, Container, Row, Col } from 'react-bootstrap';
import { useAuth } from '../../context/AuthContext';
import { useNavigate } from 'react-router-dom';
import './LoginForm.css';

const LoginForm = () => {
  const [credentials, setCredentials] = useState({
    login: '',
    password: '',
    userType: 'employee'
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const { login } = useAuth();
  const navigate = useNavigate();

  const handleChange = (e) => {
    const { name, value } = e.target;
    setCredentials(prev => ({
      ...prev,
      [name]: value
    }));
    if (error) setError('');
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!credentials.login.trim() || !credentials.password.trim()) {
      setError('Ÿäÿ±ÿ¨Ÿâ ŸÖŸÑÿ° ÿ¨ŸÖŸäÿπ ÿßŸÑÿ≠ŸÇŸàŸÑ ÿßŸÑŸÖÿ∑ŸÑŸàÿ®ÿ©');
      return;
    }

    setLoading(true);
    setError('');

    try {
      const logMsg = `üîê ŸÖÿ≠ÿßŸàŸÑÿ© ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿØÿÆŸàŸÑ... ${new Date().toISOString()}`;
      console.log(logMsg);
      sessionStorage.setItem('lastLoginAttempt', logMsg);
      
      const result = await login(credentials, credentials.userType === 'client');
      
      const resultMsg = `üìä ŸÜÿ™Ÿäÿ¨ÿ© ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿØÿÆŸàŸÑ: ${JSON.stringify(result)}`;
      console.log(resultMsg);
      sessionStorage.setItem('lastLoginResult', resultMsg);
      
      if (result.success) {
        const successMsg = `‚úÖ ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿØÿÆŸàŸÑ ŸÜÿßÿ¨ÿ≠ - Token: ${localStorage.getItem('token')?.substring(0, 20)}...`;
        console.log(successMsg);
        sessionStorage.setItem('lastLoginSuccess', successMsg);
        
        // ÿ•ÿ∂ÿßŸÅÿ© ÿ™ÿ£ÿÆŸäÿ± ÿµÿ∫Ÿäÿ± ŸÑŸÑÿ™ÿ£ŸÉÿØ ŸÖŸÜ ÿ≠ŸÅÿ∏ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™
        await new Promise(resolve => setTimeout(resolve, 500));
        
        const navMsg = `üîÑ ÿ¨ÿßÿ±Ÿä ÿßŸÑÿßŸÜÿ™ŸÇÿßŸÑ ÿ•ŸÑŸâ ŸÑŸàÿ≠ÿ© ÿßŸÑÿ™ÿ≠ŸÉŸÖ...`;
        console.log(navMsg);
        sessionStorage.setItem('lastNavigation', navMsg);
        navigate('/dashboard', { replace: true });
      } else {
        const errMsg = result.message || 'ÿßÿ≥ŸÖ ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ ÿ£Ÿà ŸÉŸÑŸÖÿ© ÿßŸÑŸÖÿ±Ÿàÿ± ÿ∫Ÿäÿ± ÿµÿ≠Ÿäÿ≠ÿ©';
        setError(errMsg);
        console.error('‚ùå Login failed:', errMsg);
        sessionStorage.setItem('lastLoginError', errMsg);
      }
    } catch (err) {
      const errorMsg = 'ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£ ŸÅŸä ÿßŸÑŸÜÿ∏ÿßŸÖ. Ÿäÿ±ÿ¨Ÿâ ÿßŸÑŸÖÿ≠ÿßŸàŸÑÿ© ŸÖÿ±ÿ© ÿ£ÿÆÿ±Ÿâ ŸÑÿßÿ≠ŸÇÿßŸã.';
      setError(errorMsg);
      console.error('‚ùå Login error:', err);
      sessionStorage.setItem('lastCatchError', err.toString());
    } finally {
      setLoading(false);
    }
  };

  return (
    <Container className="login-container">
      <Row className="justify-content-center">
        <Col xl={6} lg={7} md={8} sm={10}>
          <Card className="login-card">
            <Card.Body>
              {/* Header */}
              <div className="text-center mb-4">
                <div className="login-logo">
                  ŸÖ
                </div>
                <h3 className="login-title">ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿØÿÆŸàŸÑ</h3>
                <p className="login-subtitle">ÿßÿÆÿ™ÿ± ŸÜŸàÿπ ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ Ÿàÿ£ÿØÿÆŸÑ ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑÿØÿÆŸàŸÑ</p>
              </div>

              {/* Error Message */}
              {error && (
                <Alert variant="danger" className="text-center">
                  <span className="me-2">‚ö†Ô∏è</span>
                  {error}
                </Alert>
              )}

              {/* Login Form */}
              <Form onSubmit={handleSubmit} noValidate>
                {/* User Type */}
                <Form.Group className="mb-4">
                  <Form.Label>ŸÜŸàÿπ ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ</Form.Label>
                  <Form.Select 
                    name="userType"
                    value={credentials.userType}
                    onChange={handleChange}
                    required
                    disabled={loading}
                  >
                    <option value="employee">ŸÖŸàÿ∏ŸÅ / ŸÖÿØŸäÿ±</option>
                    <option value="client">ÿπŸÖŸäŸÑ</option>
                  </Form.Select>
                </Form.Group>

                {/* Username */}
                <Form.Group className="mb-4">
                  <Form.Label>ÿßÿ≥ŸÖ ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ</Form.Label>
                  <Form.Control
                    type="text"
                    name="login"
                    value={credentials.login}
                    onChange={handleChange}
                    placeholder="ÿ£ÿØÿÆŸÑ ÿßÿ≥ŸÖ ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ ÿßŸÑÿÆÿßÿµ ÿ®ŸÉ"
                    required
                    disabled={loading}
                    autoComplete="username"
                    autoFocus
                  />
                </Form.Group>

                {/* Password */}
                <Form.Group className="mb-4">
                  <Form.Label>ŸÉŸÑŸÖÿ© ÿßŸÑŸÖÿ±Ÿàÿ±</Form.Label>
                  <Form.Control
                    type="password"
                    name="password"
                    value={credentials.password}
                    onChange={handleChange}
                    placeholder="ÿ£ÿØÿÆŸÑ ŸÉŸÑŸÖÿ© ÿßŸÑŸÖÿ±Ÿàÿ± ÿßŸÑÿÆÿßÿµÿ© ÿ®ŸÉ"
                    required
                    disabled={loading}
                    autoComplete="current-password"
                  />
                </Form.Group>

                {/* Submit Button */}
                <Button 
                  variant="primary" 
                  type="submit" 
                  className="w-100 login-btn"
                  disabled={loading}
                >
                  {loading ? (
                    <>
                      <span 
                        className="spinner-border spinner-border-sm me-2" 
                        role="status" 
                        aria-hidden="true"
                      ></span>
                      ÿ¨ÿßÿ±Ÿä ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿØÿÆŸàŸÑ...
                    </>
                  ) : (
                    'ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿØÿÆŸàŸÑ'
                  )}
                </Button>
              </Form>

              {/* Footer Note */}
              <div className="text-center mt-4 pt-4 border-top">
                <small className="text-muted">
                  ŸÑŸÑÿπŸÖŸÑÿßÿ°: ÿ≥Ÿäÿ™ŸÖ ÿ•ÿπÿ∑ÿßÿ§ŸÉ ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑÿØÿÆŸàŸÑ ŸÖŸÜ ÿ•ÿØÿßÿ±ÿ© ÿßŸÑŸÖŸÉÿ™ÿ®
                </small>
              </div>
            </Card.Body>
          </Card>
        </Col>
      </Row>
    </Container>
  );
};

export default LoginForm;
