};

// ŸÖŸÉŸàŸÜ ÿµŸÅ ÿßŸÑŸÖŸÑŸÅ
const FileRow = ({ file, onUpdate }) => {
  const { isAdmin, isEmployee } = useAuth();

  const getRowClassName = (file) => {
    if (file.status === 'paid') {
      return 'table-row-paid';
    } else if (file.status === 'partially_paid') {
      return 'table-row-partial';
    } else if (file.status === 'closed') {
      return 'table-row-closed';
    }
    return '';
  };

  const getStatusVariant = (status) => {
    const variants = {
      new: 'primary',
      in_progress: 'warning',
      paid: 'success',
      partially_paid: 'info',
      closed: 'secondary'
    };
    return variants[status] || 'secondary';
  };

  const getStatusText = (status) => {
    const texts = {
      new: 'ÿ¨ÿØŸäÿØ',
      in_progress: 'ŸÇŸäÿØ ÿßŸÑÿπŸÖŸÑ',
      paid: 'ŸÖÿØŸÅŸàÿπ',
      partially_paid: 'ŸÖÿØŸÅŸàÿπ ÿ¨ÿ≤ÿ¶ŸäÿßŸã',
      closed: 'ŸÖÿ∫ŸÑŸÇ'
    };
    return texts[status] || status;
  };

  return (
    <tr className={getRowClassName(file)}>
      <td>
        <strong>#{file.id}</strong>
      </td>
      <td>
        <div>
          <div className="fw-semibold">{file.debtor}</div>
          {file.debt_proof && (
            <small className="text-muted">{file.debt_proof}</small>
          )}
        </div>
      </td>
      <td>
        {file.client_name || 'ÿ∫Ÿäÿ± ŸÖÿ≠ÿØÿØ'}
      </td>
      <td>
        <span className="fw-bold text-success">
          ${file.total_amount?.toLocaleString()}
        </span>
        {file.status === 'partially_paid' && file.recovery_percentage > 0 && (
          <div className="recovery-percentage mt-1">
            <small className="text-warning fw-bold">
              {file.recovery_percentage.toFixed(1)}% ŸÖÿ≥ÿ™ÿÆŸÑÿµ
            </small>
          </div>
        )}
      </td>
      <td>
        {new Date(file.deposit_date).toLocaleDateString('ar-EG')}
      </td>
      <td>
        <Badge bg={getStatusVariant(file.status)}>
          {getStatusText(file.status)}
        </Badge>
      </td>
      <td>
        <div className="action-buttons">
          <Button
            as={Link}
            to={`/files/${file.id}`}
            variant="outline-primary"
            size="sm"
            className="me-1"
          >
            <i className="fas fa-eye"></i>
          </Button>
          
          {(isAdmin() || isEmployee()) && (
            <>
              <Button
                as={Link}
                to={`/files/${file.id}/edit`}
                variant="outline-secondary"
                size="sm"
                className="me-1"
              >
                <i className="fas fa-edit"></i>
              </Button>
            </>
          )}
        </div>
      </td>
    </tr>
  );
};

// ‚úÖ ŸÖŸàÿØÿßŸÑ ÿ•ŸÜÿ¥ÿßÿ° ŸÖŸÑŸÅ ÿ¨ÿØŸäÿØ - VERSION MODIFI√âE AVEC AUTOCOMPL√âTION
const CreateFileModal = ({ show, onHide, onSuccess }) => {
  const { token } = useAuth();
  const [formData, setFormData] = useState({
    deposit_date: new Date().toISOString().split('T')[0],
    client_name: '', // ‚úÖ CHANG√â: client_id ‚Üí client_name
    debtor: '',
    debt_proof: '',
    total_amount: '',
    commission: '',
    notes: ''
  });

  const [clients, setClients] = useState([]);
  const [filteredClients, setFilteredClients] = useState([]); // ‚úÖ NOUVEAU
  const [showSuggestions, setShowSuggestions] = useState(false); // ‚úÖ NOUVEAU
  const [selectedClient, setSelectedClient] = useState(null); // ‚úÖ NOUVEAU
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  useEffect(() => {
    if (show) {
      fetchClients();
    }
  }, [show]);

  const fetchClients = async () => {
    try {
      const response = await clientsAPI.getAll();
      // backend returns { success: true, data: [...] }
      const data = response.data?.data || response.data;
      setClients(data || []);
    } catch (error) {
      console.error('Error fetching clients:', error);
    }
  };

  // ‚úÖ NOUVELLE FONCTION: G√©rer le changement du nom du client
  const handleClientNameChange = (e) => {
    const value = e.target.value;
    setFormData({ ...formData, client_name: value });
    setSelectedClient(null);

    if (value.trim() === '') {
      setFilteredClients([]);
      setShowSuggestions(false);
    } else {
      // Filtrer les clients existants
      const filtered = clients.filter(client =>
        client.name.toLowerCase().includes(value.toLowerCase())
      );
      setFilteredClients(filtered);
      setShowSuggestions(true);
    }
  };

  // ‚úÖ NOUVELLE FONCTION: S√©lectionner un client depuis les suggestions
  const handleSelectClient = (client) => {
    setSelectedClient(client);
    setFormData({ ...formData, client_name: client.name });
    setFilteredClients([]);
    setShowSuggestions(false);
  };

  // ‚úÖ NOUVELLE FONCTION: Cr√©er un nouveau client ou utiliser celui existant
  const handleCreateOrUseClient = async () => {
    if (!formData.client_name.trim()) {
      setError('ÿßŸÑÿ±ÿ¨ÿßÿ° ÿ•ÿØÿÆÿßŸÑ ÿßÿ≥ŸÖ ÿßŸÑÿπŸÖŸäŸÑ');
      return null;
    }

    let clientId = null;

    // V√©rifier si le client existe
    const existingClient = clients.find(
      c => c.name.toLowerCase() === formData.client_name.toLowerCase()
    );

    if (existingClient) {
      clientId = existingClient._id || existingClient.id;
      setSelectedClient(existingClient);
    } else {
      // Cr√©er un nouveau client
        try {
        const login = formData.client_name
          .toLowerCase()
          .replace(/\s+/g, '.')
          .substring(0, 20);
        
        const password = Math.random().toString(36).substring(2, 10);
        const response = await clientsAPI.create({
          name: formData.client_name,
          login: login,
          password: password,
          role: 'client'
        });

        const created = response.data?.data || response.data;
        clientId = created._id || created.id || created.insertId || created.ID;
        setSelectedClient(created);
        setClients([...clients, created]);
        setError('');
        
        console.log(`‚úÖ ÿπŸÖŸäŸÑ ÿ¨ÿØŸäÿØ ÿ™ŸÖ ÿ•ŸÜÿ¥ÿßÿ§Ÿá: ${formData.client_name}`);
        console.log(`üìß ÿßÿ≥ŸÖ ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ: ${login}`);
        console.log(`üîë ŸÉŸÑŸÖÿ© ÿßŸÑŸÖÿ±Ÿàÿ±: ${password}`);
        
      } catch (err) {
        setError('ÿÆÿ∑ÿ£ ŸÅŸä ÿ•ŸÜÿ¥ÿßÿ° ÿßŸÑÿπŸÖŸäŸÑ');
        console.error(err);
        return null;
      }
    }

    return clientId;
  };

  // ‚úÖ MODIFI√âE: Soumettre le formulaire
  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setLoading(true);

    try {
      // Cr√©er ou r√©cup√©rer le client
      const clientId = await handleCreateOrUseClient();

      if (!clientId) {
        setLoading(false);
        return;
      }

      // Cr√©er le dossier avec le client_id
      const dataToSubmit = {
        deposit_date: formData.deposit_date,
        client_id: clientId,
        debtor: formData.debtor,
        debt_proof: formData.debt_proof,
        total_amount: formData.total_amount,
        commission: formData.commission,
        notes: formData.notes
      };

      await filesAPI.create(dataToSubmit);
      
      // R√©initialiser le formulaire
      setFormData({
        deposit_date: new Date().toISOString().split('T')[0],
        client_name: '',
        debtor: '',
        debt_proof: '',
        total_amount: '',
        commission: '',
        notes: ''
      });
      setSelectedClient(null);
      setFilteredClients([]);
      setShowSuggestions(false);

      onSuccess();
    } catch (error) {
      setError(error.response?.data?.message || 'ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£ ÿ£ÿ´ŸÜÿßÿ° ÿ•ŸÜÿ¥ÿßÿ° ÿßŸÑŸÖŸÑŸÅ');
      console.error('Error:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  return (
    <Modal show={show} onHide={onHide} size="lg" centered>
      <Modal.Header closeButton>
        <Modal.Title>ÿ•ÿ∂ÿßŸÅÿ© ŸÖŸÑŸÅ ÿ¨ÿØŸäÿØ</Modal.Title>
      </Modal.Header>
      <Form onSubmit={handleSubmit}>
        <Modal.Body>
          {error && <Alert variant="danger">{error}</Alert>}
          
          <Row>
            <Col md={6}>
              <Form.Group className="mb-3">
                <Form.Label>ÿ™ÿßÿ±ŸäÿÆ ÿßŸÑÿ•ŸäÿØÿßÿπ *</Form.Label>
                <Form.Control
                  type="date"
                  name="deposit_date"
                  value={formData.deposit_date}
                  onChange={handleChange}
                  required
                />
              </Form.Group>
            </Col>
            <Col md={6}>
              {/* ‚úÖ NOUVEAU CHAMP: Autocompl√©tion pour ÿßŸÑÿπŸÖŸäŸÑ */}
              <Form.Group className="mb-3">
                <Form.Label>ÿßŸÑÿπŸÖŸäŸÑ *</Form.Label>
                
                <div style={{ position: 'relative' }}>
                  <Form.Control
                    type="text"
                    value={formData.client_name}
                    onChange={handleClientNameChange}
                    placeholder="ÿ£ÿØÿÆŸÑ ÿßÿ≥ŸÖ ÿßŸÑÿπŸÖŸäŸÑ ÿ£Ÿà ÿßÿÆÿ™ÿ± ŸÖŸÜ ÿßŸÑŸÇÿßÿ¶ŸÖÿ©"
                    autoComplete="off"
                    required
                  />

                  {/* ŸÇÿßÿ¶ŸÖÿ© ÿßŸÑÿßŸÇÿ™ÿ±ÿßÿ≠ÿßÿ™ */}
                  {showSuggestions && filteredClients.length > 0 && (
                    <div 
                      style={{
                        position: 'absolute',
                        top: '100%',
                        left: 0,
                        right: 0,
                        background: 'white',
                        border: '2px solid #0d6efd',
                        borderTop: 'none',
                        maxHeight: '200px',
                        overflowY: 'auto',
                        zIndex: 1000,
                        borderRadius: '0 0 4px 4px',
                        listStyle: 'none',
                        padding: 0,
                        margin: 0,
                        marginTop: '-4px',
                        boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)'
                      }}
                    >
                      {filteredClients.map(client => (
                        <div
                          key={client._id || client.id}
                          onClick={() => handleSelectClient(client)}
                          style={{
                            padding: '0.75rem',
                            cursor: 'pointer',
                            borderBottom: '1px solid #eee',
                            transition: 'background-color 0.2s',
                            display: 'flex',
                            justifyContent: 'space-between',
                            alignItems: 'center'
                          }}
                          onMouseEnter={(e) => e.currentTarget.style.backgroundColor = '#f0f8f0'}
                          onMouseLeave={(e) => e.currentTarget.style.backgroundColor = 'white'}
                        >
                          <span style={{ fontWeight: 600 }}>{client.name}</span>
                          <span 
                            style={{
                              background: '#28a745',
                              color: 'white',
                              padding: '0.25rem 0.75rem',
                              borderRadius: '20px',
                              fontSize: '0.75rem',
                              fontWeight: 600
                            }}
                          >
                            ŸÖŸàÿ¨ŸàÿØ
                          </span>
                        </div>
                      ))}
                    </div>
                  )}

                  {/* ÿ±ÿ≥ÿßŸÑÿ© ÿπŸÜÿØ ÿπÿØŸÖ Ÿàÿ¨ŸàÿØ ÿπŸÖŸÑÿßÿ° ŸÖÿ∑ÿßÿ®ŸÇŸäŸÜ */}
                  {showSuggestions && formData.client_name.trim() && filteredClients.length === 0 && (
                    <div 
                      style={{
                        position: 'absolute',
                        top: '100%',
                        left: 0,
                        right: 0,
                        background: '#e8f5e9',
                        border: '2px solid #28a745',
                        borderTop: 'none',
                        padding: '1rem',
                        borderRadius: '0 0 4px 4px',
                        zIndex: 1000,
                        marginTop: '-4px',
                        textAlign: 'center'
                      }}
                    >
                      <p style={{ margin: 0, color: '#2e7d32', fontSize: '0.9rem' }}>
                        ‚ú® ÿ≥Ÿäÿ™ŸÖ ÿ•ŸÜÿ¥ÿßÿ° ÿπŸÖŸäŸÑ ÿ¨ÿØŸäÿØ: <strong>{formData.client_name}</strong>
                      </p>
                    </div>
                  )}
                </div>

                {/* ÿπÿ±ÿ∂ ÿßŸÑÿπŸÖŸäŸÑ ÿßŸÑŸÖÿÆÿ™ÿßÿ± */}
                {selectedClient && (
                  <small style={{
                    display: 'block',
                    marginTop: '0.5rem',
                    padding: '0.5rem',
                    background: '#e8f5e9',
                    borderLeft: '4px solid #28a745',
                    borderRadius: '4px',
                    color: '#2e7d32',
                    fontSize: '0.85rem'
                  }}>
                    ‚úÖ ÿ™ŸÖ ÿßŸÑÿ™ÿ≠ÿØŸäÿØ: {selectedClient.name}
                  </small>
                )}
              </Form.Group>
            </Col>
          </Row>

          <Row>
            <Col md={6}>
              <Form.Group className="mb-3">
                <Form.Label>ÿßÿ≥ŸÖ ÿßŸÑŸÖÿØŸäŸÜ *</Form.Label>
                <Form.Control
                  type="text"
                  name="debtor"
                  value={formData.debtor}
                  onChange={handleChange}
                  placeholder="ÿ£ÿØÿÆŸÑ ÿßÿ≥ŸÖ ÿßŸÑŸÖÿØŸäŸÜ"
                  required
                />
              </Form.Group>
            </Col>
            <Col md={6}>
              <Form.Group className="mb-3">
                <Form.Label>ÿ≥ŸÜÿØ ÿßŸÑÿØŸäŸÜ</Form.Label>
                <Form.Control
                  type="text"
                  name="debt_proof"
                  value={formData.debt_proof}
                  onChange={handleChange}
                  placeholder="ŸàÿµŸÅ ÿ≥ŸÜÿØ ÿßŸÑÿØŸäŸÜ"
                />
              </Form.Group>
            </Col>
          </Row>

          <Row>
            <Col md={6}>
              <Form.Group className="mb-3">
                <Form.Label>ÿßŸÑŸÖÿ®ŸÑÿ∫ ÿßŸÑÿ•ÿ¨ŸÖÿßŸÑŸä *</Form.Label>
                <Form.Control
                  type="number"
                  name="total_amount"
                  value={formData.total_amount}
                  onChange={handleChange}
                  placeholder="ÿ£ÿØÿÆŸÑ ÿßŸÑŸÖÿ®ŸÑÿ∫"
                  required
                  min="0"
                  step="0.01"
                />
              </Form.Group>
            </Col>
            <Col md={6}>
              <Form.Group className="mb-3">
                <Form.Label>ÿßŸÑÿπŸÖŸàŸÑÿ©</Form.Label>
                <Form.Control
                  type="number"
                  name="commission"
                  value={formData.commission}
                  onChange={handleChange}
                  placeholder="ÿßŸÑÿπŸÖŸàŸÑÿ©"
                  min="0"
                  step="0.01"
                />
              </Form.Group>
            </Col>
          </Row>

          <Form.Group className="mb-3">
            <Form.Label>ŸÖŸÑÿßÿ≠ÿ∏ÿßÿ™</Form.Label>
            <Form.Control
              as="textarea"
              rows={3}
              name="notes"
              value={formData.notes}
              onChange={handleChange}
              placeholder="ÿ£Ÿä ŸÖŸÑÿßÿ≠ÿ∏ÿßÿ™ ÿ•ÿ∂ÿßŸÅŸäÿ©"
            />
          </Form.Group>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={onHide}>
            ÿ•ŸÑÿ∫ÿßÿ°
          </Button>
          <Button variant="primary" type="submit" disabled={loading}>
            {loading ? 'ÿ¨ÿßÿ±Ÿä ÿßŸÑÿ≠ŸÅÿ∏...' : 'ÿ≠ŸÅÿ∏ ÿßŸÑŸÖŸÑŸÅ'}
          </Button>
        </Modal.Footer>
      </Form>
    </Modal>
  );
};

