import React, { createContext, useState, useContext, useEffect } from 'react';

const AuthContext = createContext();

export const useAuth = () => {
  const context = useContext(AuthContext);
  if (!context) {
    throw new Error('useAuth must be used within an AuthProvider');
  }
  return context;
};

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  useEffect(() => {
    checkAuth();
  }, []);

  const checkAuth = async () => {
    try {
      const token = localStorage.getItem('token');
      const userData = localStorage.getItem('user');
      
      if (token && userData) {
        setUser(JSON.parse(userData));
        setIsAuthenticated(true);
      }
    } catch (error) {
      console.error('Auth check error:', error);
      logout();
    } finally {
      setLoading(false);
    }
  };

  const login = async (credentials, isClient = false) => {
    try {
      console.log('جاري تسجيل الدخول:', { credentials, isClient });
      
      // محاكاة اتصال API (انتظار 1 ثانية)
      await new Promise(resolve => setTimeout(resolve, 1000));
      
      // بيانات مستخدمين تجريبية
      const testUsers = {
        employee: [
          { 
            login: 'admin', 
            password: '123456', 
            role: 'admin', 
            name: 'مدير النظام', 
            id: 1,
            email: 'admin@office.com'
          },
          { 
            login: 'employee', 
            password: '123456', 
            role: 'employee', 
            name: 'موظف تجريبي', 
            id: 2,
            email: 'employee@office.com'
          }
        ],
        client: [
          { 
            login: 'client1', 
            password: '123456', 
            name: 'عميل تجريبي 1', 
            id: 101,
            phone: '123456789',
            address: 'عنوان العميل 1'
          },
          { 
            login: 'client2', 
            password: '123456', 
            name: 'عميل تجريبي 2', 
            id: 102,
            phone: '987654321', 
            address: 'عنوان العميل 2'
          }
        ]
      };

      const userType = isClient ? 'client' : 'employee';
      const userList = testUsers[userType];
      
      // البحث عن المستخدم
      const foundUser = userList.find(u => 
        u.login === credentials.login && u.password === credentials.password
      );

      if (foundUser) {
        const token = 'demo-token-' + Date.now();
        const userInfo = { 
          ...foundUser, 
          type: userType,
          token: token
        };
        
        // حفظ في localStorage
        localStorage.setItem('token', token);
        localStorage.setItem('user', JSON.stringify(userInfo));
        
        // تحديث الحالة
        setUser(userInfo);
        setIsAuthenticated(true);
        
        console.log('✅ تم تسجيل الدخول بنجاح:', userInfo);
        return { success: true, data: userInfo };
      } else {
        const errorMsg = 'اسم المستخدم أو كلمة المرور غير صحيحة';
        console.error('❌ فشل تسجيل الدخول:', errorMsg);
        return { success: false, message: errorMsg };
      }

    } catch (error) {
      console.error('❌ خطأ في تسجيل الدخول:', error);
      return { 
        success: false, 
        message: 'حدث خطأ في النظام: ' + error.message 
      };
    }
  };

  const logout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    setUser(null);
    setIsAuthenticated(false);
    console.log('✅ تم تسجيل الخروج');
  };

  const value = {
    user,
    loading,
    isAuthenticated,
    login,
    logout,
    hasRole: (role) => user?.role === role,
    isAdmin: () => user?.role === 'admin',
    isEmployee: () => user?.role === 'employee',
    isClient: () => user?.type === 'client',
  };

  return (
    <AuthContext.Provider value={value}>
      {children}
    </AuthContext.Provider>
  );
};
