import React, { useState } from 'react';
import { Form, Button, Card, Alert, Container, Row, Col } from 'react-bootstrap';
import { useAuth } from '../../context/AuthContext';
import { useNavigate } from 'react-router-dom';
import './LoginForm.css';

const LoginForm = () => {
  const [credentials, setCredentials] = useState({
    login: '',
    password: '',
    userType: 'employee'
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const { login } = useAuth();
  const navigate = useNavigate();

  const handleChange = (e) => {
    const { name, value } = e.target;
    setCredentials(prev => ({
      ...prev,
      [name]: value
    }));
    // Clear error when user starts typing
    if (error) setError('');
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    // Validation des champs
    if (!credentials.login.trim() || !credentials.password.trim()) {
      setError('يرجى ملء جميع الحقول المطلوبة');
      return;
    }

    if (credentials.password.length < 3) {
      setError('كلمة المرور يجب أن تكون على الأقل 3 أحرف');
      return;
    }

    setLoading(true);
    setError('');

    try {
      const result = await login(credentials, credentials.userType === 'client');
      
      if (result.success) {
        navigate('/dashboard', { replace: true });
      } else {
        setError(result.message || 'اسم المستخدم أو كلمة المرور غير صحيحة');
      }
    } catch (err) {
      setError('حدث خطأ في النظام. يرجى المحاولة مرة أخرى لاحقاً.');
      console.error('Login error:', err);
    } finally {
      setLoading(false);
    }
  };

  return (
    <Container className="login-container">
      <Row className="justify-content-center">
        <Col md={8} lg={6} xl={4}>
          <Card className="login-card">
            <Card.Body className="p-5">
              {/* Header */}
              <div className="text-center mb-4">
                <div className="mb-3">
                  <div style={{
                    width: '60px',
                    height: '60px',
                    background: 'linear-gradient(135deg, var(--primary-blue) 0%, var(--secondary-blue) 100%)',
                    borderRadius: '12px',
                    margin: '0 auto',
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'center',
                    color: 'white',
                    fontSize: '1.5rem',
                    fontWeight: 'bold'
                  }}>
                    م
                  </div>
                </div>
                <h3 className="login-title">تسجيل الدخول</h3>
                <p className="login-subtitle">اختر نوع المستخدم وأدخل بيانات الدخول</p>
              </div>

              {/* Error Alert */}
              {error && (
                <Alert variant="danger" className="text-center">
                  <i className="fas fa-exclamation-circle me-2"></i>
                  {error}
                </Alert>
              )}

              {/* Login Form */}
              <Form onSubmit={handleSubmit} noValidate>
                {/* User Type */}
                <Form.Group className="mb-3">
                  <Form.Label>نوع المستخدم</Form.Label>
                  <Form.Select 
                    name="userType"
                    value={credentials.userType}
                    onChange={handleChange}
                    required
                    disabled={loading}
                    className="form-select-custom"
                  >
                    <option value="employee">موظف / مدير</option>
                    <option value="client">عميل</option>
                  </Form.Select>
                </Form.Group>

                {/* Username */}
                <Form.Group className="mb-3">
                  <Form.Label>اسم المستخدم</Form.Label>
                  <Form.Control
                    type="text"
                    name="login"
                    value={credentials.login}
                    onChange={handleChange}
                    placeholder="أدخل اسم المستخدم الخاص بك"
                    required
                    disabled={loading}
                    autoComplete="username"
                    autoFocus
                  />
                </Form.Group>

                {/* Password */}
                <Form.Group className="mb-4">
                  <Form.Label>كلمة المرور</Form.Label>
                  <Form.Control
                    type="password"
                    name="password"
                    value={credentials.password}
                    onChange={handleChange}
                    placeholder="أدخل كلمة المرور الخاصة بك"
                    required
                    disabled={loading}
                    autoComplete="current-password"
                  />
                </Form.Group>

                {/* Submit Button */}
                <Button 
                  variant="primary" 
                  type="submit" 
                  className="w-100 login-btn"
                  disabled={loading}
                >
                  {loading ? (
                    <>
                      <span className="spinner-border spinner-border-sm me-2" role="status"></span>
                      جاري تسجيل الدخول...
                    </>
                  ) : (
                    <>
                      <i className="fas fa-sign-in-alt me-2"></i>
                      تسجيل الدخول
                    </>
                  )}
                </Button>
              </Form>

              {/* Footer Note */}
              <div className="text-center mt-4 pt-3 border-top">
                <small className="text-muted">
                  <i className="fas fa-info-circle me-2"></i>
                  للعملاء: سيتم إعطاؤك بيانات الدخول من إدارة المكتب
                </small>
              </div>
            </Card.Body>
          </Card>
        </Col>
      </Row>
    </Container>
  );
};

export default LoginForm;
